mapper.select:
SELECT
    b.*,
    d.name AS deviceName
FROM device_borrow b
         LEFT JOIN device d ON b.device_id = d.id
WHERE b.borrower_id = #{borrowerId}

    AND b.device_id LIKE CONCAT('%', #{deviceId}, '%')


    AND d.name LIKE CONCAT('%', #{deviceName}, '%')


    AND b.return_status = #{returnStatus}


    AND DATE(b.borrow_time) >= #{borrowTimeStart}


    AND DATE(b.borrow_time) <= #{borrowTimeEnd}

ORDER BY b.borrow_time DESC

SELECT b.*, d.name AS deviceName FROM device_borrow b LEFT JOIN device d ON b.device_id = d.id WHERE 1=1
AND b.device_id LIKE CONCAT('%', #{deviceId}, '%')
AND d.name LIKE CONCAT('%', #{deviceName}, '%')
AND b.borrower_name LIKE CONCAT('%', #{borrowerName}, '%')
AND b.department LIKE CONCAT('%', #{department}, '%')
AND b.return_status = #{returnStatus}
AND DATE(b.borrow_time) >= #{borrowTimeStart}
AND DATE(b.borrow_time) <= #{borrowTimeEnd}
ORDER BY b.borrow_time DESC
